--
-- 表的结构 `zr_newscate`
--

CREATE TABLE IF NOT EXISTS `zr_newscate` (
  `newscate_id` int(11) NOT NULL AUTO_INCREMENT,
  `newscate_name` varchar(100) DEFAULT NULL,
  `newscate_img` varchar(100) DEFAULT NULL,
  `newscate_insert_date` varchar(100) DEFAULT NULL,
  `newscate_tab` varchar(50) DEFAULT NULL,
  `seo_newscate_keywords` varchar(100) DEFAULT NULL,
  `seo_newscate_description` varchar(100) DEFAULT NULL,
  `seo_newscate_title` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`newscate_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- 导出表中的数据 `zr_newscate`
--

INSERT INTO `zr_newscate` (`newscate_id`, `newscate_name`, `newscate_img`, `newscate_insert_date`, `newscate_tab`, `seo_newscate_keywords`, `seo_newscate_description`, `seo_newscate_title`) VALUES
(1, '中禾动态', '5a5db9efd26bf.png', '1516091887', 'zhdt', '中禾动态', '中禾新材料有限公司是一家土工膜厂家,公司主要生产土工布,土工膜价格,复合土工膜,hdpe土工膜,hdpe防渗膜,土工膜,复合土工膜,土工格栅,土工材料等行业良性发展的倡导者,电话:1392388610', '中禾动态'),
(2, '行业新闻', '5a5dbac879449.png', '1516092104', 'hyxw', '环保新闻最新消息,环保资讯,环保热点,环保政策趋势', '行业新闻频道提供行业环保新闻跟进最新消息，环保资讯，环保热点，环保政策趋势等报道。', '行业新闻'),
(3, '技术文章', '5a5dbad77df61.png', '1516092119', 'jswz', '技术文章', '中禾新材料有限公司是一家优质的土工材料生产厂家,公司主要的土工膜，土工布,土工格栅,防水毯,复合土工膜,复合土工布,复合排水网,排水板,土工袋,咨询热线:13923886105。', '技术文章'),
(4, '政策新闻', NULL, NULL, NULL, '政策新闻', '中禾新材料有限公司是一家优质的土工材料生产厂家,公司主要的土工膜，土工布,土工格栅,防水毯,复合土工膜,复合土工布,复合排水网,排水板,土工袋,咨询热线:13923886105。', '政策新闻'),
(5, '环保知识', NULL, '1534212949', 'hbzs', '环保知识', '中禾新材料有限公司是一家优质的土工材料生产厂家,公司主要的土工膜，土工布,土工格栅,防水毯,复合土工膜,复合土工布,复合排水网,排水板,土工袋,咨询热线:13923886105。', '环保知识'),
(6, '土工膜文章', NULL, '1542270606', '', '土工膜', '这里有最新、最全、最快捷的土工膜新闻。', '你不懂的土工膜');

