(14, '', '玻纤土工格栅', '', '1513216743', '5ae2f9116b758.jpg', 3, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;0&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;8&quot;&gt;&lt;p&gt;玻璃纤维土工格栅技术参数&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;0&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;2&quot;&gt;&lt;p&gt;分类&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;63&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;2&quot;&gt;&lt;p&gt;型号&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;0&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot;&gt;&lt;p&gt;断裂强度(KN/m)&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;0&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot;&gt;&lt;p&gt;断裂伸长率(%)&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;67&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;2&quot;&gt;&lt;p&gt;网格尺寸(mm×mm)&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;132&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;2&quot;&gt;&lt;p&gt;幅宽(m）&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;44&quot; align=&quot;left&quot; valign=&quot;center&quot;&gt;&lt;p&gt;经向&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;48&quot; align=&quot;left&quot; valign=&quot;center&quot;&gt;&lt;p&gt;纬向&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;42&quot; align=&quot;left&quot; valign=&quot;center&quot;&gt;&lt;p&gt;经向&lt;/p&gt;&lt;/td&gt;&lt;td width=&quot;42&quot; align=&quot;left&quot; valign=&quot;center&quot;&gt;&lt;p&gt;纬向&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;78&quot; height=&quot;0&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;7&quot;&gt;&lt;p&gt;玻璃纤维土工格栅&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;GG2525&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥25&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥25&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG3030&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥30&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥30&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG4040&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥40&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥40&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG5050&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG8080&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥80&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥80&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG100100&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥100&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥100&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≤3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;12～50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;1～6&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;GG120120&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;≥120&lt;/p&gt;&lt;/