(20, '防水毯', '钠基膨润土防水毯', '', '1513217144', '5ae2fda04c3cd.jpg', 4, 0, 0, '&lt;table class=&quot;table table-bordered&quot; data-sort=&quot;sortDisabled&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;序号&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;项目&lt;br/&gt;&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;3&quot; style=&quot;word-break: break-all;&quot;&gt;技术指标&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-NP&lt;br/&gt;&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-OF&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-AH&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;01&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土防水毯单位面积质量（g/m&lt;sup&gt;2&lt;/sup&gt;）&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;02&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土膨胀指数（mL/2g）&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;br/&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;03&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;吸蓝量(g/100g)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;04&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;拉伸强度(n/100mm)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥600&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥700&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥600&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;05&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;大负荷下伸长率(%)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥10&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥10&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥8&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;06&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;剥离强度(n/100mm)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;非织造布与编织布&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥40&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥40&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;07&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;PE膜与非织造布&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;08&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;渗透系数(m/s)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10＜5.0×10&lt;sup&gt;-11&lt;/sup&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10&lt;sup&gt;-12&lt;/sup&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10&lt;sup&gt;-12&lt;/sup&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;09&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;耐静水压&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;10&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;滤失量(ml)&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;11&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土耐失性（mL/2g）&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;br/&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '钠基膨润土防水毯', '钠基膨润土防水毯，钠基膨润土防水毯价格，钠基膨润土防水毯厂家', '“钠基膨润土防水毯(GCL) ”又叫“膨润土防水毯”、“GCL膨润土垫”“防水毯”，是一种新型土工合成材料。它由经过配级的钠基膨润土颗粒和相应的外加剂混合为原材料，采用针刺法、针刺覆膜法或胶粘法把膨润土颗粒固定在土工布和塑料编织布之间而制成的毯状防水卷材。膨润土防水毯既具有土工材料的全部特性，又具有优异的防水（渗）性能。 钠基膨润土又称之为钠基蒙脱石，它遇水可以膨胀24倍以上并经过针刺工艺形成非常稳定的胶状防水层。具有滤失量低和黏性高的特性。是目前新型、环保、生态的防水卷材。', '&lt;p style=&quot;white-space: normal;&quot;&gt;克重&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;3500g-6000g/m&lt;sup&gt;2&lt;/sup&gt;&lt;/span&gt;,幅宽2m、3m、6m（常用6m）&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;30-50m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;膨润土防水毯按生产工艺可分为：针刺法钠基膨润土防水毯、针刺覆膜法钠基膨润土防水毯以及胶粘法钠基膨润土防水毯。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;针刺覆膜法钠基膨润土防水毯，是在针刺钠基膨润土防水毯的非织造土工布的外表面上复合一层高密度聚乙烯土工膜。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;胶粘法钠基膨润土防水毯，是用胶粘剂把膨润土颗粒粘结到高密度聚乙烯板上，压缩生产的一种钠基膨润土防水毯。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;国内工程使用以针刺法及针刺覆膜法居多。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '&lt;p&gt;“钠基膨润土防水毯(GCL) ”又叫“膨润土防水毯”、“GCL膨润土垫”“防水毯”，是一种新型土工合成材料。它由经过配级的钠基膨润土颗粒和相应的外加剂混合为原材料，采用&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;针刺法、针刺覆膜法&lt;/span&gt;或&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;胶粘法&lt;/span&gt;把膨润土颗粒固定在土工布和塑料编织布之间而制成的毯状防水卷材。膨润土防水毯既具有土工材料的全部特性，又具有优异的防水（渗）性能。 钠基膨润土又称之为钠基蒙脱石，它遇水可以膨胀24倍以上并经过针刺工艺形成非常稳定的胶状防水层。具有滤失量低和黏性高的特性。是目前新型、环保、生态的防水卷材。&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;防水机理为：膨润土防水毯所选用的钠基粒子膨润土遇水可膨胀24倍以上，使其形成均匀的黏性高且滤失量低的胶体系统，在两层土工布限制作用下，使膨润土从无序变为有序的膨胀，持续的吸水膨胀结果是让膨润土层自身达到密实，从而具有防水作用。抗渗静水压可达1.0MPa以上，渗透性5×10-11m/s ，单位面积膨润土质量5kg/m2，膨润土是无机材料，不会发生老化反应， 耐久性好，且不会对环境造成任何不利影响，属环保材料。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;1. 密实性：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;钠基膨润土在水压状态下形成高密度横隔膜，厚度约3mm时，它的透水性为5×10ˉ11m/s以下，相当于100倍的30cm厚度粘土的密实度，具有很强的自保水性能。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;2. 的防水性能:&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;因为钠基膨润土系无机材料，即使经过很长时间或周围环境发生变化，也不会发生老化或腐蚀现象,因此防水性能。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;3. 施工简便：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;和其他防水材料比较，施工相对比较简单，不需要加热和粘贴。只需用膨润土粉末和钉子、垫圈等进行连接和固定。施工后不需要特别的检查，如果发现防水缺陷也容易维修。GCL是现有防水材料中施工工期短的。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;4. 不受气温影响：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;在寒冷气候条件下也不会脆断。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;5. 防水材料和对象的：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;钠基膨润土遇水时，具有20~28倍的膨胀能力，即使混凝土结构物发生震动和沉降，GCL内的膨润土也能修补2mm以内混凝土表面的裂纹。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;6. 绿色环保：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;膨润土为无机材料，对环境没有特别的影响，具有良好的环保性能。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;增强路基，可地分配扩散载荷，提高路基的稳定性和承载力，延长使用寿命；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;可承受更大的交变载荷；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防止路基材料流失造成的路基变形、开裂； 单向塑料土工格栅&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;使挡土墙后的填土自承能力提高，减少挡土墙的土压力，节省费用，延长使用寿命，并降低维修费用；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;结合喷锚混凝土施工方法进行边坡维护，不仅可节省30%—50%的投资，而且可以缩短工期一倍以上；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在公路的路基和面层中加入土工格栅，可以降低弯沉，减少车辙，推迟裂缝出现时间3—9倍，可减少结构层厚度达36%；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;适用于各种土壤，无需异地取材，省工省时；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;施工简单快捷，可大大降低施工成本。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;h4 style=&quot;white-space: normal;&quot;&gt;一、注意事项&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;膨润土复合防水垫（GCL）的储运应防水、防潮、防强烈阳光爆晒。储存时地面应采取架空方法垫起，运现场的膨润土复合防水垫（GCL）应在当日用完。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在进行下道工序或相邻工程施工时，应对已完成工序的膨润土复合防水垫（GCL）妥善保护，不得有任何人为损坏。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;应尽量避免穿钉鞋、高跟鞋在膨润土复合防水垫（GCL）上踩踏； 车辆等机械不得碾压膨润土复合防水垫（GCL）。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在GCL复合衬垫的施工中，已铺完成的GCL应在当日完成HDPE膜的施工或铺设300mm厚黏土保护层，当日不能完成HDPE膜或黏土保护层的施工，应对GCL进行覆盖，以防下雨下雪而使GCL行水化及强烈阳光对GCL的爆晒。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;对GCL单层衬垫的防渗、防水施工工程，在施工结束并验收合格后，应迅速用比重大于2.0的土、砂子或石粒进行回填。回填时为防止GCL的损坏，回填土中不能含有粒径大于10mm以上的碎石，回填土厚度不应少于30cm。回填土应分级回填夯实，压实度不能低于85%。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;二、 施工方法&lt;/h4&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;1、地下室外墙施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;施工前先墙面处理干净，如有大于20cm的裂缝，缺口或蜂窝、麻面等，须用水泥砂浆修平，大于20cm的凸出物应剔除。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;建议采用由下往上的顺序铺贴外墙面防水垫。防水垫可用2mm长钢钉固定，也可用射钉枪固定，钉距为30cm；防水垫应以品字形分布，尽量避免有十字形叠口出现，重叠部分应大于10 cm；除了在防水垫搭接部位和边缘部位需要用钉固定以外，整幅防水垫中间也需视其平整度加钉固定，务实防水垫紧凑安装在墙面上，钉孔的处理用膨润土胶泥，抹于钉盖及垫片处。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;外墙下面的防水垫要和底板预留的防水垫相接、固定，以达到形成一个完整的防水体系。接近地坪时在防水垫的收口部位用铁制贴条压住，并用钢钉固定，然后涂浆状膨润土封口。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;穿墙管道和预留洞采用膨胀止水条辅之以浆状膨润土密封。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫在墙面施工搭接时，可采取以下两种方法施工：&lt;/p&gt;&lt;p&gt;1).膨润土胶泥涂抹于搭接缝处。&lt;/p&gt;&lt;p&gt;2).用膨润土防水板裁成10cm宽的条状，夹在搭接缝的中间，然后用钉固定。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫铺贴时，应使编织布一侧朝向墙体，使无纺布面向外，即朝向回填土一侧。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;墙体的施工缝也须用膨润土胶泥填于其中。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;墙面施工的阴、阳角处应作成“八”字形部位加膨润土防水棒，防水棒可用钉子及细铁丝等固定。铺贴防水垫时，把防水棒包在其内，特别是底板与墙体的阳角处。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫施工搭接时，应按照高处的防水垫在外侧，低处的防水垫在内侧（紧贴在墙面上），以防水回填土时有异物进入搭接缝。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;回填土施工：&lt;/p&gt;&lt;p&gt;1).为避免回填及夯实施工时对防水垫造成损坏，可考虑加防护板。防护板可用任何材质，只要能起到保护作用即可。&lt;/p&gt;&lt;p&gt;2).回填土应分级回填夯实，其密实度应可能短的时间内进行回填；&lt;/p&gt;&lt;p&gt;3).防水垫施工及检验完成后，应在尽可能短的时间内进行回填；&lt;/p&gt;&lt;p&gt;4).回填时应掉其中的石块、木块、混凝土块或其它有尖角的杂质。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫外墙面施工的检验：其检验内容除底板检验的项目以外，需对下列项目进行检验：&lt;/p&gt;&lt;p&gt;1).穿墙管部位的施工是否符合要求；&lt;/p&gt;&lt;p&gt;2).固定钉的间距是否符合要求；&lt;/p&gt;&lt;p&gt;3).防水垫是否牢固；&lt;/p&gt;&lt;p&gt;4).回填土是否达到要求的密实度。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;2、填埋场的施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;GCL的铺设：&lt;/p&gt;&lt;p&gt;因铺设场工程均为施工场面积大，作业时间较长，因此制定合理的施工方案，防水垫的施工须分若干工作面进行，并需与HDPE膜的施工进行协调。&lt;/p&gt;&lt;p&gt;1).填埋场工程适用于宽幅、大卷的防水垫，铺设时宜采用机械施工。&lt;/p&gt;&lt;p&gt;2).分块分区域进行防水垫的铺设。&lt;/p&gt;&lt;p&gt;3).坡度较大的斜面可用塑料锚固钉加以固定。&lt;/p&gt;&lt;p&gt;4).铺设时应使防水垫的无纺布面朝下（即朝向垫层一侧），编织布面朝上（即朝准备铺土工膜的一侧）&lt;/p&gt;&lt;p&gt;5).所有已铺设完的防水垫，在当天完成其上层HDPE膜的施工，以确保不会因雨雪天气或太阳爆晒而使防水垫失去防水作用，因此要求事先制定好合理的施工方案。&lt;/p&gt;&lt;p&gt;6).若填埋场的设计仅用单层防水垫（GCL）作为防渗层时，在防水垫铺设完成后，应于当日完成回填土的施工并压实。&lt;/p&gt;&lt;p&gt;7).建议防水垫的搭接宽度≥30cm。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;3、人工湖的施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;基层处理土质的基层要求平整并夯实，其上不得有草根及粒度≥20mm的碎石等杂质。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫的裁切及铺设:&lt;/p&gt;&lt;p&gt;本公司所产防水垫规格为幅宽≤6米，长度≤60米。裁切时可根据施工尺寸裁切，可横向铺设，也可纵向铺设。防水垫铺设时其所有接缝（横向或纵向）处只需搭接，并在接缝处按0.4kg/m2的数量撒上膨润土干粉（膨润土胶泥）即可。接缝宽度≥25cm,一旦防水垫出现破损、发现有孔洞等缺陷或损伤时，应及时用膨润土干粉（膨润土胶泥）或用破损部位尺寸放大15cm以上的GCL及膨润土干粉（膨润土胶泥）进行局部覆盖修补即可，边缘部位按搭接的要求处理，当坡度较大时，可用专用铆固钉固定以避免防水垫产生滑动。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;保护层的施工:&lt;/p&gt;&lt;p&gt;当使用预制混凝土砌块作保护层时，可在铺设完成的防水垫上均匀铺铺撒约3-5cm厚的细砂或素土（细砂或素土中不能含有粒度大于2cm的石子或其它杂物）。然后进行混凝土砌块保护层的施工，也可直接在防水垫上用水泥砂浆砌筑混凝土砌块，其施工方法及要求同膜料防渗时保护层的施工要求。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;三、施工注意事项&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;膨润土防水毯可在负温下使用，一般不宜在负温度下进行施工。在负温度下，屋面保温层、找平层不易干燥，其中的多余水分如遭冻结，体积膨胀，容易导致卷材防水层开裂；待气温上升解冻时，水分蒸发，又容易造成卷材防水层起鼓。另外，由于冬季温度低，散热快，不易保证膨润土防水毯胶结材料的使用温度。防水卷材在负温度下容易脆断，铺贴也比较困难，很难保证防水层的质量。&lt;/p&gt;&lt;p&gt;当有在冬季铺贴膨润土防水毯屋面防水层时，找平层宜采用现浇的膨润土防水毯砂浆，或用已经干燥的钢筋混凝土预制板，板缝之间用膨润土防水毯砂浆嵌填。&lt;/p&gt;&lt;p&gt;施工时，应采取防寒措施，确保膨润土防水毯胶结材料的使用温度，否则很难达到规范要求粘贴膨润土防水毯的每层热膨润土防水毯胶结材料的厚度不大于1.5mm的规定。负温度下屋面防水层，好采取先铺贴一层防水卷材层，确保该工程的屋面不渗漏，等气温转暖，经检查整修后，再按原设计要求铺贴好膨润土防水毯层和保护层&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;膨润土防水毯基面处理:&lt;/p&gt;&lt;p&gt;1).潮湿度要求&lt;/p&gt;&lt;p&gt;膨润土颗粒的吸水性很强,因此在生产、仓储和运输过程中要求保持干燥,但在施工过程中,只要求基层表面无明水流即可,一般的潮湿基面不会导致膨润土毯提前膨胀,如基面流水无法处理时,要求进行临时引排。&lt;/p&gt;&lt;p&gt;2).平整度要求&lt;/p&gt;&lt;p&gt;在对基面进行处理时,要求基面的平整度(凹坑的大深度与小直径的比值)不大于1/6,且要求平缓过渡,不得出现直角等部位。但对于凹凸起伏比较规则的桩结构,其表面的平整度要求满足转弯半径不小于40 cm即可,此时要对底板和侧墙连接部位进行处理,保证底板和侧墙处膨润土毯能够平滑过渡。 除要求膨润土毯密贴在基层表面且不出现大的皱褶外,还 要处理好基层表面的毛糙部位。因为在喷射混凝土基面直接铺设膨润土毯时,毯体与基层的接触面间往往存在很多微小的悬空部位,无法密贴,这样在膨润土膨胀时,会先填补这些微小孔洞,造成膨润土颗粒的损失,而一旦地下水位反复变化时,会膨润土颗粒带走,影响膨润土毯的防水效果。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;市政工程，地铁、建筑物的地下工程和房顶蓄水池的防渗。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;环保环卫，生活垃圾填埋场，污水处理场，工业废弃物。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;水利，江河湖泊水库堤坝的防渗，堵漏，加固。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;园林人工湖高尔夫球场的水塘防渗等。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;石化、矿业、农业等领域工程的防渗、防腐、防漏、加固等。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;防渗&lt;/span&gt;、&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;防水&lt;/span&gt;&lt;/p&gt;');
INSERT INTO `zr_product` (`product_id`, `product_name`, `product_info`, `product_content`, `product_insert_date`, `product_img`, `product_pid`, `product_show_type`, `product_alis`, `product_index`, `product_title`, `product_keywords`, `product_description`, `product_config`, `list_order`, `product_intro`, `product_specialty`, `product_usage`, `product_eng`, `product_apply`, `product_function`) VALUES
(21, '防水毯', 'GCL膨润土防水毯', '', '1513217246', '5ae2fdcbc4ead.jpg', 4, 0, 0, '&lt;table class=&quot;table table-bordered&quot; data-sort=&quot;sortDisabled&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;序号&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;项目&lt;br/&gt;&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;3&quot; style=&quot;word-break: break-all;&quot;&gt;技术指标&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-NP&lt;br/&gt;&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-OF&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;GCL-AH&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;01&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土防水毯单位面积质量（g/m&lt;sup&gt;2&lt;/sup&gt;）&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥4000且不小于规定值&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;02&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土膨胀指数（mL/2g）&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;br/&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥24&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;03&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;吸蓝量(g/100g)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;04&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;拉伸强度(n/100mm)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥600&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥700&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥600&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;05&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;大负荷下伸长率(%)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥10&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥10&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥8&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;06&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; rowspan=&quot;2&quot; colspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;剥离强度(n/100mm)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;非织造布与编织布&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥40&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥40&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;07&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;PE膜与非织造布&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;≥30&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;/&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;08&lt;/td&gt;&lt;td valign=&quot;top&quot; rowspan=&quot;1&quot; colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;渗透系数(m/s)&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10＜5.0×10&lt;sup&gt;-11&lt;/sup&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10&lt;sup&gt;-12&lt;/sup&gt;&lt;/td&gt;&lt;td width=&quot;155&quot; valign=&quot;top&quot; style=&quot;word-break: break-all;&quot;&gt;＜5.0×10&lt;sup&gt;-12&lt;/sup&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;09&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;耐静水压&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;0.4MPa,1h无渗漏&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;10&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;滤失量(ml)&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≤18&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;11&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;2&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;膨润土耐失性（mL/2g）&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;/td&gt;&lt;td valign=&quot;top&quot; colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥20&lt;br/&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', 'GCL膨润土防水毯', 'GCL膨润土防水毯，GCL膨润土防水毯价格，GCL膨润土防水毯厂家', '“钠基膨润土防水毯(GCL) ”又叫“膨润土防水毯”、“GCL膨润土垫”“防水毯”，是一种新型土工合成材料。它由经过配级的钠基膨润土颗粒和相应的外加剂混合为原材料，采用针刺法、针刺覆膜法或胶粘法把膨润土颗粒固定在土工布和塑料编织布之间而制成的毯状防水卷材。膨润土防水毯既具有土工材料的全部特性，又具有优异的防水（渗）性能。 钠基膨润土又称之为钠基蒙脱石，它遇水可以膨胀24倍以上并经过针刺工艺形成非常稳定的胶状防水层。具有滤失量低和黏性高的特性。是目前新型、环保、生态的防水卷材。', '&lt;p style=&quot;white-space: normal;&quot;&gt;克重&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;3500g-6000g/m&lt;sup&gt;2&lt;/sup&gt;&lt;/span&gt;,幅宽2m、3m、6m（常用6m）&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;30-50m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;膨润土防水毯按生产工艺可分为：针刺法钠基膨润土防水毯、针刺覆膜法钠基膨润土防水毯以及胶粘法钠基膨润土防水毯。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;针刺覆膜法钠基膨润土防水毯，是在针刺钠基膨润土防水毯的非织造土工布的外表面上复合一层高密度聚乙烯土工膜。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;胶粘法钠基膨润土防水毯，是用胶粘剂把膨润土颗粒粘结到高密度聚乙烯板上，压缩生产的一种钠基膨润土防水毯。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;国内工程使用以针刺法及针刺覆膜法居多。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '&lt;p&gt;“钠基膨润土防水毯(GCL) ”又叫“膨润土防水毯”、“GCL膨润土垫”“防水毯”，是一种新型土工合成材料。它由经过配级的钠基膨润土颗粒和相应的外加剂混合为原材料，采用&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;针刺法、针刺覆膜法&lt;/span&gt;或&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;胶粘法&lt;/span&gt;把膨润土颗粒固定在土工布和塑料编织布之间而制成的毯状防水卷材。膨润土防水毯既具有土工材料的全部特性，又具有优异的防水（渗）性能。 钠基膨润土又称之为钠基蒙脱石，它遇水可以膨胀24倍以上并经过针刺工艺形成非常稳定的胶状防水层。具有滤失量低和黏性高的特性。是目前新型、环保、生态的防水卷材。&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;防水机理为：膨润土防水毯所选用的钠基粒子膨润土遇水可膨胀24倍以上，使其形成均匀的黏性高且滤失量低的胶体系统，在两层土工布限制作用下，使膨润土从无序变为有序的膨胀，持续的吸水膨胀结果是让膨润土层自身达到密实，从而具有防水作用。抗渗静水压可达1.0MPa以上，渗透性5×10-11m/s ，单位面积膨润土质量5kg/m2，膨润土是无机材料，不会发生老化反应， 耐久性好，且不会对环境造成任何不利影响，属环保材料。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;1. 密实性：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;钠基膨润土在水压状态下形成高密度横隔膜，厚度约3mm时，它的透水性为5×10ˉ11m/s以下，相当于100倍的30cm厚度粘土的密实度，具有很强的自保水性能。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;2. 的防水性能:&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;因为钠基膨润土系无机材料，即使经过很长时间或周围环境发生变化，也不会发生老化或腐蚀现象,因此防水性能。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;3. 施工简便：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;和其他防水材料比较，施工相对比较简单，不需要加热和粘贴。只需用膨润土粉末和钉子、垫圈等进行连接和固定。施工后不需要特别的检查，如果发现防水缺陷也容易维修。GCL是现有防水材料中施工工期短的。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;4. 不受气温影响：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;在寒冷气候条件下也不会脆断。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;5. 防水材料和对象的：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;钠基膨润土遇水时，具有20~28倍的膨胀能力，即使混凝土结构物发生震动和沉降，GCL内的膨润土也能修补2mm以内混凝土表面的裂纹。&lt;/p&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;6. 绿色环保：&lt;/h5&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;膨润土为无机材料，对环境没有特别的影响，具有良好的环保性能。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;增强路基，可地分配扩散载荷，提高路基的稳定性和承载力，延长使用寿命；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;可承受更大的交变载荷；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防止路基材料流失造成的路基变形、开裂； 单向塑料土工格栅&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;使挡土墙后的填土自承能力提高，减少挡土墙的土压力，节省费用，延长使用寿命，并降低维修费用；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;结合喷锚混凝土施工方法进行边坡维护，不仅可节省30%—50%的投资，而且可以缩短工期一倍以上；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在公路的路基和面层中加入土工格栅，可以降低弯沉，减少车辙，推迟裂缝出现时间3—9倍，可减少结构层厚度达36%；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;适用于各种土壤，无需异地取材，省工省时；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;施工简单快捷，可大大降低施工成本。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;h4 style=&quot;white-space: normal;&quot;&gt;一、注意事项&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;膨润土复合防水垫（GCL）的储运应防水、防潮、防强烈阳光爆晒。储存时地面应采取架空方法垫起，运现场的膨润土复合防水垫（GCL）应在当日用完。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在进行下道工序或相邻工程施工时，应对已完成工序的膨润土复合防水垫（GCL）妥善保护，不得有任何人为损坏。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;应尽量避免穿钉鞋、高跟鞋在膨润土复合防水垫（GCL）上踩踏； 车辆等机械不得碾压膨润土复合防水垫（GCL）。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在GCL复合衬垫的施工中，已铺完成的GCL应在当日完成HDPE膜的施工或铺设300mm厚黏土保护层，当日不能完成HDPE膜或黏土保护层的施工，应对GCL进行覆盖，以防下雨下雪而使GCL行水化及强烈阳光对GCL的爆晒。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;对GCL单层衬垫的防渗、防水施工工程，在施工结束并验收合格后，应迅速用比重大于2.0的土、砂子或石粒进行回填。回填时为防止GCL的损坏，回填土中不能含有粒径大于10mm以上的碎石，回填土厚度不应少于30cm。回填土应分级回填夯实，压实度不能低于85%。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;二、 施工方法&lt;/h4&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;1、地下室外墙施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;施工前先墙面处理干净，如有大于20cm的裂缝，缺口或蜂窝、麻面等，须用水泥砂浆修平，大于20cm的凸出物应剔除。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;建议采用由下往上的顺序铺贴外墙面防水垫。防水垫可用2mm长钢钉固定，也可用射钉枪固定，钉距为30cm；防水垫应以品字形分布，尽量避免有十字形叠口出现，重叠部分应大于10 cm；除了在防水垫搭接部位和边缘部位需要用钉固定以外，整幅防水垫中间也需视其平整度加钉固定，务实防水垫紧凑安装在墙面上，钉孔的处理用膨润土胶泥，抹于钉盖及垫片处。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;外墙下面的防水垫要和底板预留的防水垫相接、固定，以达到形成一个完整的防水体系。接近地坪时在防水垫的收口部位用铁制贴条压住，并用钢钉固定，然后涂浆状膨润土封口。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;穿墙管道和预留洞采用膨胀止水条辅之以浆状膨润土密封。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫在墙面施工搭接时，可采取以下两种方法施工：&lt;/p&gt;&lt;p&gt;1).膨润土胶泥涂抹于搭接缝处。&lt;/p&gt;&lt;p&gt;2).用膨润土防水板裁成10cm宽的条状，夹在搭接缝的中间，然后用钉固定。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫铺贴时，应使编织布一侧朝向墙体，使无纺布面向外，即朝向回填土一侧。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;墙体的施工缝也须用膨润土胶泥填于其中。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;墙面施工的阴、阳角处应作成“八”字形部位加膨润土防水棒，防水棒可用钉子及细铁丝等固定。铺贴防水垫时，把防水棒包在其内，特别是底板与墙体的阳角处。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫施工搭接时，应按照高处的防水垫在外侧，低处的防水垫在内侧（紧贴在墙面上），以防水回填土时有异物进入搭接缝。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;回填土施工：&lt;/p&gt;&lt;p&gt;1).为避免回填及夯实施工时对防水垫造成损坏，可考虑加防护板。防护板可用任何材质，只要能起到保护作用即可。&lt;/p&gt;&lt;p&gt;2).回填土应分级回填夯实，其密实度应可能短的时间内进行回填；&lt;/p&gt;&lt;p&gt;3).防水垫施工及检验完成后，应在尽可能短的时间内进行回填；&lt;/p&gt;&lt;p&gt;4).回填时应掉其中的石块、木块、混凝土块或其它有尖角的杂质。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫外墙面施工的检验：其检验内容除底板检验的项目以外，需对下列项目进行检验：&lt;/p&gt;&lt;p&gt;1).穿墙管部位的施工是否符合要求；&lt;/p&gt;&lt;p&gt;2).固定钉的间距是否符合要求；&lt;/p&gt;&lt;p&gt;3).防水垫是否牢固；&lt;/p&gt;&lt;p&gt;4).回填土是否达到要求的密实度。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;2、填埋场的施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;GCL的铺设：&lt;/p&gt;&lt;p&gt;因铺设场工程均为施工场面积大，作业时间较长，因此制定合理的施工方案，防水垫的施工须分若干工作面进行，并需与HDPE膜的施工进行协调。&lt;/p&gt;&lt;p&gt;1).填埋场工程适用于宽幅、大卷的防水垫，铺设时宜采用机械施工。&lt;/p&gt;&lt;p&gt;2).分块分区域进行防水垫的铺设。&lt;/p&gt;&lt;p&gt;3).坡度较大的斜面可用塑料锚固钉加以固定。&lt;/p&gt;&lt;p&gt;4).铺设时应使防水垫的无纺布面朝下（即朝向垫层一侧），编织布面朝上（即朝准备铺土工膜的一侧）&lt;/p&gt;&lt;p&gt;5).所有已铺设完的防水垫，在当天完成其上层HDPE膜的施工，以确保不会因雨雪天气或太阳爆晒而使防水垫失去防水作用，因此要求事先制定好合理的施工方案。&lt;/p&gt;&lt;p&gt;6).若填埋场的设计仅用单层防水垫（GCL）作为防渗层时，在防水垫铺设完成后，应于当日完成回填土的施工并压实。&lt;/p&gt;&lt;p&gt;7).建议防水垫的搭接宽度≥30cm。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h5 style=&quot;white-space: normal;&quot;&gt;3、人工湖的施工&lt;/h5&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;基层处理土质的基层要求平整并夯实，其上不得有草根及粒度≥20mm的碎石等杂质。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;防水垫的裁切及铺设:&lt;/p&gt;&lt;p&gt;本公司所产防水垫规格为幅宽≤6米，长度≤60米。裁切时可根据施工尺寸裁切，可横向铺设，也可纵向铺设。防水垫铺设时其所有接缝（横向或纵向）处只需搭接，并在接缝处按0.4kg/m2的数量撒上膨润土干粉（膨润土胶泥）即可。接缝宽度≥25cm,一旦防水垫出现破损、发现有孔洞等缺陷或损伤时，应及时用膨润土干粉（膨润土胶泥）或用破损部位尺寸放大15cm以上的GCL及膨润土干粉（膨润土胶泥）进行局部覆盖修补即可，边缘部位按搭接的要求处理，当坡度较大时，可用专用铆固钉固定以避免防水垫产生滑动。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;保护层的施工:&lt;/p&gt;&lt;p&gt;当使用预制混凝土砌块作保护层时，可在铺设完成的防水垫上均匀铺铺撒约3-5cm厚的细砂或素土（细砂或素土中不能含有粒度大于2cm的石子或其它杂物）。然后进行混凝土砌块保护层的施工，也可直接在防水垫上用水泥砂浆砌筑混凝土砌块，其施工方法及要求同膜料防渗时保护层的施工要求。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;三、施工注意事项&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;膨润土防水毯可在负温下使用，一般不宜在负温度下进行施工。在负温度下，屋面保温层、找平层不易干燥，其中的多余水分如遭冻结，体积膨胀，容易导致卷材防水层开裂；待气温上升解冻时，水分蒸发，又容易造成卷材防水层起鼓。另外，由于冬季温度低，散热快，不易保证膨润土防水毯胶结材料的使用温度。防水卷材在负温度下容易脆断，铺贴也比较困难，很难保证防水层的质量。&lt;/p&gt;&lt;p&gt;当有在冬季铺贴膨润土防水毯屋面防水层时，找平层宜采用现浇的膨润土防水毯砂浆，或用已经干燥的钢筋混凝土预制板，板缝之间用膨润土防水毯砂浆嵌填。&lt;/p&gt;&lt;p&gt;施工时，应采取防寒措施，确保膨润土防水毯胶结材料的使用温度，否则很难达到规范要求粘贴膨润土防水毯的每层热膨润土防水毯胶结材料的厚度不大于1.5mm的规定。负温度下屋面防水层，好采取先铺贴一层防水卷材层，确保该工程的屋面不渗漏，等气温转暖，经检查整修后，再按原设计要求铺贴好膨润土防水毯层和保护层&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;膨润土防水毯基面处理:&lt;/p&gt;&lt;p&gt;1).潮湿度要求&lt;/p&gt;&lt;p&gt;膨润土颗粒的吸水性很强,因此在生产、仓储和运输过程中要求保持干燥,但在施工过程中,只要求基层表面无明水流即可,一般的潮湿基面不会导致膨润土毯提前膨胀,如基面流水无法处理时,要求进行临时引排。&lt;/p&gt;&lt;p&gt;2).平整度要求&lt;/p&gt;&lt;p&gt;在对基面进行处理时,要求基面的平整度(凹坑的大深度与小直径的比值)不大于1/6,且要求平缓过渡,不得出现直角等部位。但对于凹凸起伏比较规则的桩结构,其表面的平整度要求满足转弯半径不小于40 cm即可,此时要对底板和侧墙连接部位进行处理,保证底板和侧墙处膨润土毯能够平滑过渡。 除要求膨润土毯密贴在基层表面且不出现大的皱褶外,还 要处理好基层表面的毛糙部位。因为在喷射混凝土基面直接铺设膨润土毯时,毯体与基层的接触面间往往存在很多微小的悬空部位,无法密贴,这样在膨润土膨胀时,会先填补这些微小孔洞,造成膨润土颗粒的损失,而一旦地下水位反复变化时,会膨润土颗粒带走,影响膨润土毯的防水效果。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;市政工程，地铁、建筑物的地下工程和房顶蓄水池的防渗。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;环保环卫，生活垃圾填埋场，污水处理场，工业废弃物。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;水利，江河湖泊水库堤坝的防渗，堵漏，加固。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;园林人工湖高尔夫球场的水塘防渗等。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;石化、矿业、农业等领域工程的防渗、防腐、防漏、加固等。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;防渗&lt;/span&gt;、&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;防水&lt;/span&gt;&lt;/p&gt;'),
(26, '排水网', '三维复合排水网', '', '1513230411', '5ae3024ef27aa.jpg', 6, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th rowspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;项目&lt;br/&gt;&lt;/th&gt;&lt;th colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;指标&lt;/th&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工排水网&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工复合排水放&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;密度/（g/cm&lt;sup&gt;3&lt;/sup&gt;）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥0.939&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;炭黑含量/%&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;2~3&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;纵向拉伸强度/（kN/m）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥8.0&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥16.0&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;纵向导水率（法向荷载500kPa、水里梯度0.1）/（m&lt;sup&gt;2&lt;/sup&gt;/s）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥3.0x10&lt;sup&gt;-3&lt;/sup&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥3.0x10&lt;sup&gt;-4&lt;/sup&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;剥离强度/（kN/m）&lt;/td&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥0.17&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工布单位面积质量/（g/m&lt;sup&gt;2&lt;/sup&gt;）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥200&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', '三维复合排水网', '三维复合排水网,三维复合排水网价格,三维复合排水网厂家', '“复合排水网”又称为“土工复合排水网”“排水网格”由立体结构的塑料网双面粘接渗水 土工布组成，可替代传统的 沙粒和砾石层，主要用于垃圾填埋场、路基和隧道内壁的排水。\r\n复合排水网按网格形态分为“三维（三肋）复合排水网”和“两维（两肋）复合排水网”，按土工布形态分为“长丝复合排水网”和“短丝复合排水网”', '&lt;p style=&quot;white-space: normal;&quot;&gt;网芯厚度&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;4-8mm&lt;/span&gt;,土工布&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;150-300g&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;幅宽&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;2m-4.6m&lt;/span&gt;,卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;50-200m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;', '', '&lt;p style=&quot;white-space: normal;&quot;&gt;“复合排水网”又称为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;土工复合排水网&lt;/span&gt;”“排水网格”由立体结构的塑料网双面粘接渗水 土工布组成，可替代传统的 沙粒和砾石层，主要用于垃圾填埋场、路基和隧道内壁的排水。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合排水网按网格形态分为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;三维&lt;/span&gt;（三肋）复合排水网”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两维&lt;/span&gt;（两肋）复合排水网”，按土工布形态分为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;长丝&lt;/span&gt;复合排水网”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;短丝&lt;/span&gt;复合排水网”&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;1.排水性强（相当于一米厚砾石排水）。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2.抗拉强度高。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3.减少土工织物嵌入网芯的机率，保持长期稳定的排水。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4.长期承受高压载荷（能承受大约3000Ka的压缩载荷）。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5.耐腐蚀、耐酸碱、使用寿命长。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;6.施工方便、缩短工期、降低成本。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合排水网的作用特性：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;铺设在地基和底基之间，用于排出地基与底基之间积水，阻断毛细水并有效地结合到边缘排水系统中。这种结构自动缩短了地基的排水途径，排水时间大大减短，而且可以使精选地基材料的使用数量减少（即可使用带有多细料，渗透率较低的材料）。可以延长道路的使用寿命。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在底基层铺设排水网能够防止底基细料进入地基层（也是起到隔离作用）。骨料基层会在有限程度内进入土工网的上层部分。这样，复合土工排水网还有一种潜在的限制骨料基层横向移动的作用，在这个方式上，它的作用类似于土工格栅的加固作用。一般来说，复合土工排水网的抗拉强度和刚性优于许多用于地基加固的土工格栅，这种限制作用提高地基的支撑能力。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;道路老化、裂缝形成后，大部分雨水会进入断面中。在这种情况下，把排水网直接铺设在路面下，代替可排水地基。排水网可以在水分进入地基/底基层之前其先行收集。而且还可以在排水网的底端包上一层膜，进一步防止水分进入地基。对刚性道路系统来说，这种结构允许用较高的排水系数Cd来设计道路。这种结构的另一个优点是可能能使混凝土均匀地水合（有关这个优点的程度研究正在进行中）。不管是对刚性道路还是柔性道路系统，这种结构都能够延长道路的使用寿命。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在北方气候条件下，铺设排水网可帮助减轻冻胀的影响。如果冰冻深度很深，可把土工网铺设在底基中较浅的位置，作为毛细作用阻塞。另外经常还需要用不易冻胀的粒状底基层替换，向下延展到冰冻深度。易冻胀的回填土可直接填在排水网上面，直到地基地面线。在这种情况下，该系统可连接到排水出口，使地下水位在相当于或低于这个深度。这样可以潜在地限制冰晶体的发展，在寒冷地区春季冰融时，不用对交通荷载进行限制。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;主要用于垃圾填埋场、路基和内壁的排水。&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;注意事项&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;土工格栅的铺筑面应较为平整，铺筑层经验收合格后，为防纵向歪斜现象，先按幅宽在铺筑层划出白线或挂线，即可开始铺筑，然后用铁钉固定格栅的端部（每米宽用钉8根，均匀距离固定），固定好格栅端部后，用铺筑机格栅缓缓向前拉铺，每铺10米长进行人工拉紧和调直一次，直一卷格栅铺完，再铺下一卷，操作同前，铺完一卷后用6T-10T的压路机从起始点开始向前进方向碾压一遍即可，（如铺筑在中面层上和找平层上，用钢辊压路机为宜；如格栅直接铺在混凝土路面上，用胶辊压路机为宜，）接铺：以卷长为单位作为铺设的段长，在应铺格栅的段长内铺满以后，再整体检查一次铺筑质量，然后接着铺筑下一段，下一段铺筑时，格栅与格栅可以用10-15CM的搭接长度，并用铁钉或木楔固定后继续向前进方向铺段，依次类推，操作要求同前。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;施工要点&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;施工场地：要求压实平整、呈水平状、尖刺突起物。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;格栅铺设：在平整压实的场地上，安装铺设的格栅其主要受力方向（纵向）应垂直于路堤轴线方向，铺设要平整，无皱折，尽量张紧。用插钉及土石压重固定，铺设的格栅主要受力方向好是通长无接头，幅与幅之间的连接可以人工绑扎搭接，搭接宽度不小于10cm。如设置的格栅在两层以上，层与层之间应错缝。大面积铺设后，要整体调整其平直度。当填盖一层土后，未碾压前，应再次用人工或机具张紧格栅，力度要均匀，使格栅在土中为绷直受力状态。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 填料的选择：填料应按设计要求选取。实践证明，除冻结土、沼泽土、生活垃圾、白垩土、硅藻土外均可用做填料。但砾类土和砂类土力学性能稳定，受含水量影响很小，宜优先选用。填料粒径不得大于15cm，并注意控制填料级配，以保证压实重量。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 填料的摊铺和压实：当格栅铺设定位后，应及时填土覆盖，裸露时间不得超48小时，亦可采取边铺设边回填的流水作业法。先在两端摊铺填料，格栅固定，再向中部推进。碾压的顺序是先两侧后中间。碾压时压轮不能直接与筋材接触，未压实的加筋体一般不允许车辆在上面行驶，以免筋材错位。分层压实度为20-30cm。压实度达到设计要求，这也是加筋土工程的成败关键。&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-indent: 28px;&quot;&gt;防排水措施：在加筋土工程中，要作好墙体内外的排水处理；要做好护脚，防冲刷；在土体内要设置滤、排水措施，时，应设置土工布、透水管（或盲沟）。采取疏导的方式排水，不能堵塞，否则产生隐患。&lt;/p&gt;', '&lt;p&gt;主要用于铁路、公路、隧道、市政工程、水库、护坡等排水工程中效果。&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, sans-serif;&quot;&gt;过滤、反滤&lt;/span&gt;&lt;/p&gt;'),
(27, '排水网', '二维复合排水网', '', '1513230553', '5ae302cd8af54.jpg', 6, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;th rowspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;项目&lt;br/&gt;&lt;/th&gt;&lt;th colspan=&quot;2&quot; style=&quot;word-break: break-all;&quot;&gt;指标&lt;/th&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工排水网&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工复合排水放&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;密度/（g/cm&lt;sup&gt;3&lt;/sup&gt;）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥0.939&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;炭黑含量/%&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;2~3&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;纵向拉伸强度/（kN/m）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥8.0&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥16.0&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;纵向导水率（法向荷载500kPa、水里梯度0.1）/（m&lt;sup&gt;2&lt;/sup&gt;/s）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥3.0x10&lt;sup&gt;-3&lt;/sup&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥3.0x10&lt;sup&gt;-4&lt;/sup&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;剥离强度/（kN/m）&lt;/td&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;td colspan=&quot;1&quot; rowspan=&quot;1&quot; style=&quot;word-break: break-all;&quot;&gt;≥0.17&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;土工布单位面积质量/（g/m&lt;sup&gt;2&lt;/sup&gt;）&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;--&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;≥200&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', '二维复合排水网_二维复合排水网价格_二维复合排水网厂家', '二维复合排水网，二维复合排水网价格，二维复合排水网厂家', '中禾新材料有限公司是一家的土工材料生产厂家,公司主要的土工膜，土工布，土工格栅，防水毯，复合土工膜，复合土工布，复合排水网，排水板，土工袋,咨询热线:13923886105。', '&lt;p style=&quot;white-space: normal;&quot;&gt;网芯厚度&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;4-8mm&lt;/span&gt;,土工布&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;150-300g&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;幅宽&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;2m-4.6m&lt;/span&gt;,卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;50-200m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;', '', '&lt;p style=&quot;white-space: normal;&quot;&gt;“复合排水网”又称为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;土工复合排水网&lt;/span&gt;”“排水网格”由立体结构的塑料网双面粘接渗水 土工布组成，可替代传统的 沙粒和砾石层，主要用于垃圾填埋场、路基和隧道内壁的排水。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合排水网按网格形态分为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;三维&lt;/span&gt;（三肋）复合排水网”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两维&lt;/span&gt;（两肋）复合排水网”，按土工布形态分为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;长丝&lt;/span&gt;复合排水网”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;短丝&lt;/span&gt;复合排水网”&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;1.排水性强（相当于一米厚砾石排水）。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2.抗拉强度高。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3.减少土工织物嵌入网芯的机率，保持长期稳定的排水。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4.长期承受高压载荷（能承受大约3000Ka的压缩载荷）。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5.耐腐蚀、耐酸碱、使用寿命长。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;6.施工方便、缩短工期、降低成本。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合排水网的作用特性：&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;铺设在地基和底基之间，用于排出地基与底基之间积水，阻断毛细水并有效地结合到边缘排水系统中。这种结构自动缩短了地基的排水途径，排水时间大大减短，而且可以使精选地基材料的使用数量减少（即可使用带有多细料，渗透率较低的材料）。可以延长道路的使用寿命。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在底基层铺设排水网能够防止底基细料进入地基层（也是起到隔离作用）。骨料基层会在有限程度内进入土工网的上层部分。这样，复合土工排水网还有一种潜在的限制骨料基层横向移动的作用，在这个方式上，它的作用类似于土工格栅的加固作用。一般来说，复合土工排水网的抗拉强度和刚性优于许多用于地基加固的土工格栅，这种限制作用提高地基的支撑能力。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;道路老化、裂缝形成后，大部分雨水会进入断面中。在这种情况下，把排水网直接铺设在路面下，代替可排水地基。排水网可以在水分进入地基/底基层之前其先行收集。而且还可以在排水网的底端包上一层膜，进一步防止水分进入地基。对刚性道路系统来说，这种结构允许用较高的排水系数Cd来设计道路。这种结构的另一个优点是可能能使混凝土均匀地水合（有关这个优点的程度研究正在进行中）。不管是对刚性道路还是柔性道路系统，这种结构都能够延长道路的使用寿命。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在北方气候条件下，铺设排水网可帮助减轻冻胀的影响。如果冰冻深度很深，可把土工网铺设在底基中较浅的位置，作为毛细作用阻塞。另外经常还需要用不易冻胀的粒状底基层替换，向下延展到冰冻深度。易冻胀的回填土可直接填在排水网上面，直到地基地面线。在这种情况下，该系统可连接到排水出口，使地下水位在相当于或低于这个深度。这样可以潜在地限制冰晶体的发展，在寒冷地区春季冰融时，不用对交通荷载进行限制。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;主要用于垃圾填埋场、路基的排水。&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;调整土工合成材料的方向，使材料卷的长度方向垂直于道路。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工排水网端接，相邻土工网芯上的土工布沿着材料卷搭接。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;把相邻的土工材料卷的土工网芯用白色或的塑料扣或聚合物带子连接起来，从而连接材料卷。沿着材料卷的长度方向，每隔3英尺用带子连接一次。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;搭接的土工织物的方向要与填料堆放的方向一致。若土工材料铺设在地基/基层和底基层之间，要进行连续楔形或缝合，使搭接的土工织物上层固定。如果缝合，使用平头缝法或一般缝法，以达到针脚长度的要求。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;主要用于铁路、公路、隧道、市政工程、水库、护坡等排水工程中效果。&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, sans-serif;&quot;&gt;过滤、反滤&lt;/span&gt;&lt;/p&gt;'),
(28, '排水板', '蓄排水板', '', '1513230746', '5ae411d418293.jpg', 7, 0, 0, '', '蓄排水板', '蓄排水板,蓄排水板价格,蓄排水板厂家', '蓄排水板采用高密度聚乙烯（HDPE）或聚丙烯（PP）经加热加压定型形成的一种既能营造具有立体空间支撑刚度的排水通道，又可蓄水的一种轻型板材。', '', '', '&lt;p&gt;蓄排水板采用高密度聚乙烯（HDPE）或聚丙烯（PP）经加热加压定型形成的一种既能营造具有立体空间支撑刚度的排水通道，又可蓄水的一种轻型板材。&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;1、施工方便、维护简便且经济。 　&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2、耐负荷及耐久性强。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3、能确保多余水快速排走。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4、蓄水部分能部分水储藏起来。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5、能给植物生长提供充足的水份与氧气。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;6、量轻及屋顶隔热保温功能强。&lt;/p&gt;', '&lt;p&gt;用于屋顶绿化、地下车顶板绿化、城市广场、高尔夫球场、运动场、污水处理厂、公共建筑绿化、广场绿化、园区内道路绿化工程。&lt;/p&gt;', '&lt;p&gt;&lt;strong&gt;蓄排水板的铺设：&lt;/strong&gt;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;防水层及其保护层施工完成后，开始铺装排水板。要求基层排水坡度满足2‰~5‰;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;排水板杯状口朝上铺装，齿孔相扣；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;排水板上面覆盖土工布，土工布相互搭接10~20cm, 防止泥水穿过土工布，淤堵蓄排水系统，造成排水不畅，蓄水量减少；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;在土工布上面铺10~20mm中粗砂，形成过滤层，可阻止种植土流失；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;覆盖适当厚度种植土；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;要做好排水板成品保护。排水板铺装后，应不容许在排水板上直接推运手推车等，运输材料时要铺设木板、马道。做到随铺随填土。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;p&gt;用于屋顶绿化、地下车顶板绿化、城市广场、高尔夫球场、运动场、污水处理厂、公共建筑绿化、广场绿化、园区内道路绿化工程。&lt;/p&gt;', '&lt;p&gt;绿化工程：车库顶板绿化、屋顶花园、垂直绿化、斜屋顶绿化、足球场、高尔夫球场。&lt;/p&gt;&lt;p&gt;市政工程：机场、道路路基、地铁、隧道、垃圾填埋场。&lt;/p&gt;&lt;p&gt;建筑工程：建筑物基础上层或下层、地下室内外墙体和底板以及顶板、屋面防渗和隔热层等。&lt;/p&gt;&lt;p&gt;水利工程：水库防渗水、蓄水池、人工湖防渗水。&lt;/p&gt;&lt;p&gt;交通工程：公路、铁路路基、堤坝和护坡层&lt;/p&gt;');
INSERT INTO `zr_product` (`product_id`, `product_name`, `product_info`, `product_content`, `product_insert_date`, `product_img`, `product_pid`, `product_show_type`, `product_alis`, `product_index`, `product_title`, `product_keywords`, `product_description`, `product_config`, `list_order`, `product_intro`, `product_specialty`, `product_usage`, `product_eng`, `product_apply`, `product_function`) VALUES
(29, '排水板', '塑料排水板', '', '1513230839', '5ae411952d421.jpg', 7, 0, 0, '', '塑料排水板', '塑料排水板,塑料排水板价格,塑料排水板厂家', '塑料排水板别名塑料排水带，有波浪型、口琴型等多种形状。中间是挤出成型的塑料芯板，是排水带的骨架和通道，其断面呈并联十字，两面以非织造土工织物包裹作滤层，芯带起支撑作用并滤层渗进来的水向上排出，是淤泥、淤质土、冲填土等饱和粘性及杂填土运用排水固结法进行软基处理的良好垂直通道，大大缩短软土固结时间。', '', '', '&lt;p&gt;塑料排水板别名塑料排水带，有波浪型、口琴型等多种形状。中间是挤出成型的塑料芯板，是排水带的骨架和通道，其断面呈并联十字，两面以非织造土工织物包裹作滤层，芯带起支撑作用并滤层渗进来的水向上排出，是淤泥、淤质土、冲填土等饱和粘性及杂填土运用排水固结法进行软基处理的良好垂直通道，大大缩短软土固结时间。&lt;/p&gt;', '&lt;p&gt;塑料排水板加固软土地基的优点：&lt;/p&gt;&lt;p&gt;1. 滤水性好，排水畅通，排水效果有保证。&lt;/p&gt;&lt;p&gt;2. 材料有良好的强度和延展性，能适合地基变形能力而不影响排水性能。&lt;/p&gt;&lt;p&gt;3. 排水板断面尺寸小，施打排水板过程中对地基扰动小。&lt;/p&gt;&lt;p&gt;4. 可在超软弱地基上进行插板施工。&lt;/p&gt;&lt;p&gt;5. 施工快、工期短，每台插板机每日可插板5000m以上，造价比袋砂井低。&lt;/p&gt;&lt;p&gt;对于深厚的软土地基采用排水固结法进行加固时，从技术上和经济上考虑，采用排水板几乎是经济、有效、可行的方法。&lt;/p&gt;', '&lt;p&gt;软基处理的通道&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;strong&gt;施工工序&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;塑料排水板施工宜在铺设砂垫层后按下列顺序进行&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;1、整平原地面&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2、摊铺下层砂垫层&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3、根据打设板位标记进行打设机定位&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4、安装管靴&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5、沉设套管&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;6、开机打设设计标高&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;7、提升套管&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;8、剪断塑料排水板&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;9、检查并记录板位等打设情况&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;10、移动打设机下一板位&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;11、摊铺上层砂垫层&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;strong&gt;注意事项&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;1、打设机定位时管靴与板位标记的偏差应控制在±70mm范围内。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2、打设过程中应随时注意控制套管垂直度，其偏差应不大于±1.5%。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3、按设计要求严格控制塑料排水板的打设标高，不得出现浅向偏差；当发现地质情况变化无法按设计要求打设时，应及时与现场监理人员联系并征得同意后方可变打设标高。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4、打设塑料排水板时严禁出现扭结断裂和撕破滤膜等现象。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5、打设时回带长度不得超过500mm，且回带的根数不宜超过打设总根数的5%。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;6、剪断塑料排水板时砂垫层以上的外露长度应大于0.5m。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;7、应检查每根板的施工情况当符合验收标准时方可移机打设下一根，否则须在邻近板位处补打。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;8、打设过程中应逐板进行自检并按要求作好施工记录塑料排水板施工原始记录表。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;9、打入地基的塑料排水板宜为整板，长度不足需要接长时按规定的方法与要求进行。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;10、一个区段塑料排水板验收合格后，应及时用砂垫层砂料仔细填满打设时在板周围形成的孔洞，并塑料排水板埋置于砂垫层中。&lt;/p&gt;&lt;p&gt;塑料排水板施工宜在铺设砂垫层后按下列顺序进行&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;整平原地面&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;摊铺下层砂垫层&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;根据打设板位标记&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;安装管靴&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;沉设套管&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;开机打设设计标高&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;提升套管&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;剪断塑料排水板&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;检查并记录板位等打设情况&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;移动打设机下一板位&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;摊铺上层砂垫层&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;p&gt;绿化工程：车库顶板绿化、屋顶花园、垂直绿化、斜屋顶绿化、足球场、高尔夫球场。&lt;/p&gt;&lt;p&gt;市政工程：机场、道路路基、地铁、隧道、垃圾填埋场。&lt;/p&gt;&lt;p&gt;建筑工程：建筑物基础上层或下层、地下室内外墙体和底板以及顶板、屋面防渗和隔热层等。&lt;/p&gt;&lt;p&gt;水利工程：水库防渗水、蓄水池、人工湖防渗水。&lt;/p&gt;&lt;p&gt;交通工程：公路、铁路路基、堤坝和护坡层&lt;/p&gt;', '&lt;p&gt;导水排水性&lt;/p&gt;&lt;p&gt;防排水保护板都有的凹凸式中空立筋结构，可以快速有效导出雨水，大大减少甚防水层的静水压，通过这种主动导水原理可以达到主动防水的效果。&lt;/p&gt;&lt;p&gt;防水性能：聚乙烯（HDPE） 聚苯乙烯（PVC）防排水保护板材料本身是一种很好的防水材料。通过采用可靠的连接方式，使防排水板成为一种很好的辅助防水材料。&lt;/p&gt;&lt;p&gt;保护防护性&lt;/p&gt;&lt;p&gt;防排水保护板可以有效地保护构筑物和防水层，并且抵抗土壤中的各类酸碱和植物的根刺。在地下室外墙回填土时，它可以保护建筑物和防水层免遭破坏。&lt;/p&gt;&lt;p&gt;　　隔音及通风防潮功能：&lt;/p&gt;&lt;p&gt;　　实验室数据表明，聚乙烯（HDPE）聚氯乙烯（PVC）防排水保护板可有效降低室内14分贝，500HZ的噪音，具有明显的减噪隔音功能。 防水导水板在地面或墙面使用时，亦可起到很好的通风防潮作用。&lt;/p&gt;'),
(36, '土工袋', '编织袋', '', '1513231389', '5ae4169e8d1e4.jpg', 9, 0, NULL, '', '编织袋', '编织袋,编织袋价格,编织袋厂家', '编织袋，又称蛇皮袋。是塑料袋的一种，用于包装，其原料一般是聚乙烯、聚丙烯等各种化学塑料原料。编织密度是指100mm×100mm编织物内，经纬纱的根数。', '', '', '&lt;p&gt;编织袋，又称蛇皮袋。是塑料袋的一种，用于包装，其原料一般是聚乙烯、聚丙烯等各种化学塑料原料。编织密度是指100mm×100mm编织物内，经纬纱的根数。&lt;/p&gt;', '&lt;p&gt;PP扁丝编织物手感较厚实，挺阔，粗硬些。&lt;/p&gt;&lt;p&gt;HDPE扁丝编织物手感较软，润滑，不致密。&lt;/p&gt;&lt;p&gt;在PP扁丝料中添加钙质母料，手感挺阔，PP中少加HDPE会使其柔软些。&lt;/p&gt;&lt;p&gt;扁丝窄，编织的平，手感柔软，扁丝宽，编织布时折叠丝多，手感粗糙。&lt;/p&gt;', '&lt;p&gt;主要用于各种物品的成装和包装用，在工业方面用途广泛。&lt;/p&gt;', '&lt;p&gt;施工注意&lt;/p&gt;&lt;p&gt;1、生态带内的植物种子处在生长期，在施工中应尽量轻拿轻放，确保种子能完好的附在生态袋上。&lt;/p&gt;&lt;p&gt;2、需在基土运达现场后再进行装袋，装好的袋子应及时码放到施工地段。&lt;/p&gt;&lt;p&gt;3、生态袋内应装干土，而不是装带水的湿土。湿的土容易对底层的生态袋造成较大压力，同时，自然的雨水滋润，也可确保植物的正常生长。&lt;/p&gt;&lt;p&gt;4、若是在一些山石或砂石较多的坡面施工，得从底部开始施工。同时在的距离，放置一根pvc管，可确保上层码放的生态袋不发生位置偏移。&lt;/p&gt;&lt;p&gt;5、对于垂直或坡面较陡的地方码放生态袋，在每叠摞1米高时，需在基面上打上固定桩，同时用绳索整层的生态袋绑紧，并分别固定在固定桩上。&lt;/p&gt;', '&lt;p&gt;包装袋&lt;/p&gt;&lt;p&gt;1.工农业产品包装袋&lt;/p&gt;&lt;p&gt;由于制品资源和价格问题,我国每年有60亿条编织袋用于水泥包装,占散装水泥包装的85%以上.随着柔性集装袋的开发应用,塑料编织集装装袋广泛用于海上,交通运输包装工农业产品,在农产品包装中,塑料编织袋已经广泛用于水产品包装,禽类饲料包装,养殖场的覆盖材料,农作物种植的遮阳,防风,防雹棚等材料. 常见产品：饲料编织袋、化工编织袋、腻子粉编织袋、尿素编织袋、蔬菜网眼袋、水果网眼袋等。&lt;/p&gt;&lt;p&gt;2.食品包装袋&lt;/p&gt;&lt;p&gt;米,面粉等食品包装逐渐采用编织袋包装. 常见的编织袋有：大米编织袋、面粉编织袋、玉米编织袋等编织袋。&lt;/p&gt;&lt;p&gt;3.旅游运输业&lt;/p&gt;&lt;p&gt;旅游业中的临时帐篷,遮阳伞,各种旅行包,旅行袋,都有塑料编织物应用,各种篷布广泛用于运输及储存的覆盖材料,取代了易于霉烂笨重的棉织篷布.施工中的围栏,网罩等也广泛应用于塑料编织物. 常见的有：物流袋、物流包装袋、货运袋、货运包装袋等&lt;/p&gt;&lt;p&gt;工程材料&lt;/p&gt;&lt;p&gt;从80年代开发土工布以来,拓宽了塑料编织物的应用领域,广泛用于小水利,电力,公路,铁路,海港,矿山建筑,军事的工程建设中.在这些工程中,土工合成材料具有过滤,排水,加强,隔离,防渗等功能,塑料土工布是合成土工材料的一种.&lt;/p&gt;&lt;p&gt;抗洪物资&lt;/p&gt;&lt;p&gt;抗洪救灾缺少不了编织袋.堤坝,河岸,铁路,公路的修建中也缺少不了编织袋. 是防讯编织袋，防旱编织袋、防洪编织袋。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', ''),
(37, '土工袋', '生态袋', '', '1513231432', '5ae41323bd03d.jpg', 9, 0, NULL, '', '生态袋', '生态袋,生态袋价格,生态袋厂家', '生态袋是一种高强抗紫外线抗冻融耐酸碱的生态合成材料，是高新技术新型特种材料，具有高强抗UV 耐酸碱 抗腐蚀 抗冻融 作可达到50年以上不降解，是真正实现了零污染。主要运用于建造柔性生态边坡。生态袋具有抗潮湿性，不吸水，水分不会破坏袋子，袋子也不会变形，不溶于污染液体，是一种保护生态的好工具', '', '', '&lt;p&gt;生态袋是一种高强抗紫外线抗冻融耐酸碱的生态合成材料，是高新技术新型特种材料，具有高强抗UV 耐酸碱 抗腐蚀 抗冻融 作可达到50年以上不降解，是真正实现了零污染。主要运用于建造柔性生态边坡。生态袋具有抗潮湿性，不吸水，水分不会破坏袋子，袋子也不会变形，不溶于污染液体，是一种保护生态的好工具&lt;/p&gt;', '&lt;p&gt;1、抗潮湿&lt;/p&gt;&lt;p&gt;生态袋原材料不吸收水分，水分出现时不会破坏袋子，而袋子也不会变形，不溶于污染液体（EPA Method 9090环境局）。&lt;/p&gt;&lt;p&gt;2、抗化学腐蚀（PH）&lt;/p&gt;&lt;p&gt;广泛研究测试表明生态袋对浓度的酸碱化学物品有很强的抵抗力，可用于绝大多数固体和严重污染地点。&lt;/p&gt;&lt;p&gt;3、抗生物降解和动物破坏&lt;/p&gt;&lt;p&gt;生态袋采用特殊配方材料，不支持、不吸收、不帮助菌类生长，不腐烂、不发霉、不变质。生态袋不会被昆虫和有关动物消化，不会成为啃齿动物（老鼠）、白蚁、蛀虫、甲壳虫、银鱼蛾等的食物。&lt;/p&gt;&lt;p&gt;4、抗紫高分子&lt;/p&gt;&lt;p&gt;生态袋可以承受150摄氏度高温而不融化，可以承受气温-40摄氏度。&lt;/p&gt;&lt;p&gt;生态袋含碳墨和其他抗UV成分。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;p&gt;主要运用于建造柔性生态边坡。&lt;/p&gt;', '&lt;p&gt;施工注意&lt;/p&gt;&lt;p&gt;1、生态带内的植物种子处在生长期，在施工中应尽量轻拿轻放，确保种子能完好的附在生态袋上。&lt;/p&gt;&lt;p&gt;2、需在基土运达现场后再进行装袋，装好的袋子应及时码放到施工地段。&lt;/p&gt;&lt;p&gt;3、生态袋内应装干土，而不是装带水的湿土。湿的土容易对底层的生态袋造成较大压力，同时，自然的雨水滋润，也可确保植物的正常生长。&lt;/p&gt;&lt;p&gt;4、若是在一些山石或砂石较多的坡面施工，得从底部开始施工。同时在的距离，放置一根pvc管，可确保上层码放的生态袋不发生位置偏移。&lt;/p&gt;&lt;p&gt;5、对于垂直或坡面较陡的地方码放生态袋，在每叠摞1米高时，需在基面上打上固定桩，同时用绳索整层的生态袋绑紧，并分别固定在固定桩上。&lt;/p&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;1. 河流两岸： 人类和野生生物共存的自然岸带环境&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2. 矿山复绿：综合利用矿矸石，科学整治沉降区，绿化荒山，净化水气，开创矿山生态修复，荒山复绿新纪元。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3. 海湖滨岸、地表滑坡治理、涵洞口、排水沟、土壤侵蚀、灌溉系统等。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;4. 人工湿地：生态修复&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;5,；屋顶绿化&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;基础建设&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;1. 道路护坡：铁路、公路护坡，适用于挖方边坡和填方边坡。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2. 河湖护坡：适用于河流、湖泊、水库边坡。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3. 特殊应用：军事设施与防洪应急、掩体、防洪堤坝等。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;园林绿化及住宅&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;1. 垂直绿化、园林艺术；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;2.商业住宅小区；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;3.屋顶花园式绿化。&lt;/p&gt;', ''),
(38, '土工袋', '集装袋', '', '1513231464', '5ae4161089a1f.jpg', 9, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;形状&lt;/p&gt;&lt;/td&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;结构&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;代号&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;3&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;圆形&lt;/p&gt;&lt;/td&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;有出料口&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;非全开口&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;YF&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;全开口&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;YQ&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;无出料口&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;YW&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; rowspan=&quot;3&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;方形&lt;/p&gt;&lt;/td&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;箱形&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;FX&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;半散形&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;FB&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;2&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;全散形&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;FQ&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', '集装袋', '集装袋,集装袋价格,集装袋厂家', '柔性集装袋又称吨袋或太空袋，是一种中型散装容器，主要以起重机或叉车可以实现集装单元化运输，它便于装运大宗散装粉状物料，具有容积大，重量轻，便于装卸等特点，是一种常见的包装材料之一。', '&lt;p&gt;中国的集装袋标准：执行的是GB/T10454-2000，这是在老标准GB/T 10454-1989上修改而成，中国集装袋标准规定了集装袋的分类结构、技术要求及检验要求等。&lt;/p&gt;&lt;p&gt;本标准适用于容积在（0.5-2.3）m3之间，载重在（500-3000）kg之间的集装袋。&lt;/p&gt;&lt;p&gt;日本标准：jis z1651-1988&lt;/p&gt;&lt;p&gt;英国标准：bs6382&lt;/p&gt;&lt;p&gt;澳大利亚标准：as3668-1989&lt;/p&gt;&lt;p&gt;欧洲标准：EN1898-2000　和　EN277-1995欧洲集装袋协会标准：EFIBCA standard&lt;/p&gt;', '', '&lt;p&gt;柔性集装袋又称吨袋或太空袋，是一种中型散装容器，主要以起重机或叉车可以实现集装单元化运输，它便于装运大宗散装粉状物料，具有容积大，重量轻，便于装卸等特点，是一种常见的包装材料之一。&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;在结构上有足够的强度，装填、卸出操作方便，适应机械化装卸，而非手工操作。操作工使用起重机或铲车来搬运货物，提高了工作效率。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;集装袋原材料的阻隔性好，结构密封性也较好，水分、杂物、灰尘不易混入，对产品的保护作用好。而且轻便、柔软、强度高，耐酸碱腐蚀，防潮、不渗漏，适用于包装粉状、片状、颗粒状固体产品，便于运输和仓储。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;无须再包装，相比于包装物，集装袋一般无须托盘，大大节约了包装成本。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;装袋可以平整折叠，捆包运输到端使用者并在卸料后折叠放置，只须占用极小的仓储空间，而且普通1 t货物所用集装袋重只有3．5～4．5kg，因此，集装袋的使用比较方便和经济。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;集装袋一般可以根据端用户的特殊需求来定做，包括袋身和上下料口的尺寸，吊带的款式，基布的规格，是否须涂膜，是否须内袋等。也可以根据产品的特性来设计。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;特别适用于机械化作业，是仓储、包装、运输的理想选择，可广泛应用于水泥、化肥、食盐、糖、化工原料、矿石等散装物质的公路、铁路及海上运输的包装。因此，近年来世界各国都广泛地采用集装袋来运输粉粒状货物。集装袋的出现和使用，是粉粒状货物装运方式的一次质的。&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;装料。先集装袋吊环对称挂于灌装机横梁上，使集装袋底基本与地面接触，然后集装袋装料口打开，套入装料筒并由夹持装置夹紧，以免粉尘或颗粒漏出。启动灌装机原料从装料口装入袋内，灌装时先袋身拉直后再灌装。灌装结束后，关闭装料口。如使用带有卸料口的集装袋，灌装前应查看底部是否扎好。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;装车。集装袋吊环均衡地挂于提升装置上，根据集装袋的类型及装载重量，可选用叉车、起重机、吊车等。如果用叉车来吊装集装袋，应铲板调整到恰当的位置，集装袋应紧贴叉车，不要向前倾斜。为保证吊装时袋体的平衡，注意切不可挂单环。然后，启动提升装置集装袋慢慢提起，到达高度后，均匀平放在火车(或汽车)台板上。如果不须立即装车运走，在工作场地应堆放整齐一致，在吊装及堆放过程中，不要相互挤压、歪斜、碰撞等。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;储存。原则上集装袋装载料后，应即装即走，但如因情况需要，不能立即运走，为避免物料损坏，集装袋应在仓库内储存，环境周围应远离水、汽、潮湿及高温源，如在室外存放时，除满足上述环境条件外，还应注意用蓬布遮盖，并避免阳光直射。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;卸料。用叉车或吊车集装袋吊起，对准料槽或容器的口，打开袋体底部料口系绳，原料可自行落下，完成卸料。对于一次性使用的集装袋可用一合适的工具远距离地袋底扎破即可卸料。在吊卸作业中，不要站在集装袋的下面。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '', ''),
(40, '复合膜', '两布一膜', '', '1513231641', '5ae2ffeac3750.jpg', 5, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;&lt;p&gt;序号&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;项 目&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;指 标&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;EVA&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;普通&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;环保&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;1&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;&lt;p&gt;厚度mm&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-4.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-4.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;宽度m&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-9.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-9.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-8.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-8.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;拉伸强度（纵横）Mpa&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=14&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=16&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=16&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=17&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=25&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;4&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;断裂伸长率（纵横）%&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=400&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=700&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=550&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=450&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=550&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;5&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;直角撕裂强度N/mm&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=60&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=60&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=80&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=110&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;6&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;水蒸气渗透系数g. cm/c㎡.s. pa &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;7&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;使用温度范围&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;8&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;炭黑含量%&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.0-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;9&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;耐环境应力开裂F&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=1500&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;-70℃低温冲击脆化性能&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;通过pass&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;11&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;200℃氧化诱导时间&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', '两布一膜', '两布一膜,两布一膜价格,两布一膜厂家', '两布一膜是以塑料薄膜作为防渗基材，与无纺布复合而成的土工防渗材料。它的防渗性能主要取决于塑料薄膜的防渗性能。目前防渗应用的塑料薄膜，主要有聚氯乙烯（PVC）和聚乙烯（PE），它们是一种高分子化学柔性材料，比重较小，延伸性较强，适应变形能力高，耐腐蚀，耐低温，抗冻性能好。膜的厚度一般为0.30-2.00mm。', '&lt;p style=&quot;white-space: normal;&quot;&gt;重量为200-1500g/m2，宽幅4-6m，&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;50-200m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;', '', '&lt;p style=&quot;white-space: normal;&quot;&gt;“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;复合土工膜&lt;/span&gt;”是用土工织物与土工膜复合而成的不透水材料，它主要用于防渗，复合土工膜是在薄膜的一侧或两侧经过烘箱远红外加热，把土工布和土工膜经导辊压到一起形成复合土工膜。随着生产工艺的提高，还有一种流延法做复合土工膜的工艺，其形式有“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;一布一膜&lt;/span&gt;”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两布一膜&lt;/span&gt;”“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两膜一布&lt;/span&gt;”等。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合土工膜抗拉、抗撕裂、顶破等物理力学性能指标高，能满足水利、市政、建筑、交通、地铁、隧道等土木工程需要。由于其选用高分子材料且生产工艺中添加了防老化剂，故可在非常规温度环境中使用。&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;抗拉、抗撕裂、顶破等物理力学性能指标高&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;产品具有强度高，延伸性能较好，变形模量大&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;耐酸碱、抗腐蚀，耐老化，防渗性能好等特点。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;广泛使用良好的平面排水，优良的防渗特性。&lt;/p&gt;', '&lt;h4 style=&quot;white-space: normal;&quot;&gt;一、材料选择&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;复合土工膜材料质量检测。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;堤坡的清理、平整场地，一切尖角杂物，欠坡回填夯实、富坡削坡挖平后经监理验收合格，为复合土工膜铺设提供工作面。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;土工膜场内拼接为了施工方便，保证拼接质量，复合土工膜应尽量采用宽幅，减少现场拼接量，施工前应根据复合土工膜幅宽、现场长度需要，在单位内剪裁，并拼接成合符要求尺寸的块体，卷在钢管上，人工搬运到工作面铺设。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;二、复合土工膜的铺设与焊接方法&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;h5&gt;铺设技术&lt;/h5&gt;&lt;p&gt;1).铺设应在干燥和暖天气进行，复合土工膜铺设和展开方向，由E向W或由W向E进行，复合土工膜的铺设技术要求为了便于拼接，防止应力集中，复合土工膜铺设采用波浪形松弛方式，富余度约为1.5%，摊开后及时拉平，拉开，要求复合土工膜与坡面吻合平整，无突起褶皱，施工人员应穿平底布鞋或软胶鞋，严禁穿钉鞋，以免踩坏土工膜，施工时如发现土工膜损坏，应及时修补。 复合土工膜在运输过程中不要拖拉、硬拽，避免尖锐物刺伤。&lt;/p&gt;&lt;p&gt;2).铺设顺序，自渠道轴线向S、N两侧推进。&lt;/p&gt;&lt;p&gt;3).应从底部向高位延伸，不要拉得太紧，应留有1.50%的余幅，以备局部下沉拉伸。&lt;/p&gt;&lt;p&gt;4).相邻两幅的纵向接头不应在一条水平线上，应相互错开1m以上；&lt;/p&gt;&lt;p&gt;5).纵向接头应距离坝脚、弯脚处1.50m以上，应设在平面上；&lt;/p&gt;&lt;p&gt;6).先边坡后场底&lt;/p&gt;&lt;p&gt;7).边坡铺设时，展膜方向应基本平行于大坡度线。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;h5&gt;焊接技术&lt;/h5&gt;&lt;p&gt;1).焊道搭接宽度：80~100mm；平面和垂直面的自然褶皱分别为：5%~8%；预留伸缩量：3%~5%；边角料剩余量：2%~5%。&lt;/p&gt;&lt;p&gt;2).热熔焊接工作温度280~300℃；行进速度2~3m/min；焊接形式为双焊道。&lt;/p&gt;&lt;p&gt;3).破损部位修复方法，裁剪规格相同的材料，热熔粘补，聚乙烯胶密封。&lt;/p&gt;&lt;p&gt;4).焊道处无仿布的连接，采用机械缝合。&lt;/p&gt;&lt;p&gt;5).水下管口的密封止水，采用GB橡胶止水条密封，金属包扎并防腐处理。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;工序流程&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;基础开挖&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;做库底砂浆保护层（H20-30mm）&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;削坡、密实与平整&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜与垂直挡水墙粘贴&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;基面平整、、工序交接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;水下水管口的密封止水&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜铺设与焊接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;工序交接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;焊接与修补质量验收&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;竣工验收&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;复合土工膜的耐静水压测定&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;h5&gt;复合土工膜的耐静水压原理&lt;/h5&gt;&lt;p&gt;在复合土工膜两侧压力水头达到值后，复合土工膜会破裂。逐级增加试样两侧水力压养，并保持时间，当渗流量急速增加，表示试样受到破坏，也获得了试样的耐静水压值。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;保持复合土工膜的上述压力少2h，观察渗流管水位变化情况，复合土工膜水位基本稳定（渗流量为0），则以0.1-0.2mpa，为级差逐级增加压力，每级均保持2h，直出现渗流量快速增加现象，表明试样已出现破裂，此前一级压力即作为耐静水压（mpa)。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜如只需判定土工膜样品是否达到某一规定耐静水压值，则可直接加压到此压力并保持2h，再判定是符合要求。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜每个样品少测定3个试样，以低值作为样品的耐静水压。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;能满足水利、市政、建筑、交通，地铁、隧道、工程建设中的防渗、隔离、补强、防裂加固等土木工程需要。常用于堤坝、排水沟渠的防渗处理，以及废料场的防污处理。&lt;/p&gt;', '&lt;p&gt;防渗、隔离、补强、防裂加固&lt;/p&gt;');
INSERT INTO `zr_product` (`product_id`, `product_name`, `product_info`, `product_content`, `product_insert_date`, `product_img`, `product_pid`, `product_show_type`, `product_alis`, `product_index`, `product_title`, `product_keywords`, `product_description`, `product_config`, `list_order`, `product_intro`, `product_specialty`, `product_usage`, `product_eng`, `product_apply`, `product_function`) VALUES
(41, '复合膜', '一布一膜', '', '1513231692', '5ae2ff8b14515.jpg', 5, 0, 0, '&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td&gt;&lt;p&gt;序号&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;项 目&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;指 标&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;EVA&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;复合土工膜&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;普通&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;环保&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;1&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;厚度mm&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-4.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;0.2-4.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;宽度m&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-9.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-9.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-8.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.5-8.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;3&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;拉伸强度（纵横）Mpa&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=14&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=16&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=16&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=17&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=25&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;4&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;断裂伸长率（纵横）%&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=400&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=700&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=550&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=450&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=550&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;5&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;直角撕裂强度N/mm&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=50&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=60&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=60&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=80&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=110&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;6&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;水蒸气渗透系数g. cm/c㎡.s. pa &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;lt;1.0*10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;7&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;使用温度范围&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;+70℃﹋-70℃&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;8&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;炭黑含量%&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;2.0-3.0&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;9&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;耐环境应力开裂F&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;&amp;gt;=1500&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;10&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;-70℃低温冲击脆化性能&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;通过pass&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;p&gt;11&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;200℃氧化诱导时间&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;p&gt;———&lt;/p&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td style=&quot;word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;', '一布一膜', '一布一膜', '一布一膜有的变形量，对底垫层的凹凸缺产生的应力传递分散较快，应变能力较强。用土工织物代替粒材作为土工膜保护层，以保护土工膜防渗层不受损坏，降低垫层粒径的级配要求，并能起排水作用。摩擦系数大，能防止覆盖层的滑移，比单纯HDPE薄膜，复合土工膜中的无纺布有较大的摩擦系数，复合土工膜可使坡比增大减少占地面积。撕裂，顶破，穿刺等力学强度较高。有的保温作用，减少了土体冻胀对土工膜的破坏，从而减少土体变形。采用埋入式铺设，有优异的抗老化性能，减少了工程的维护保养。铺设施工简便，减少运输量，降低工程造价，缩短了工期。', '&lt;p style=&quot;white-space: normal;&quot;&gt;重量为200-1500g/m2，宽幅4-6m，&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;卷长&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;50-200m&lt;/span&gt;(长度根据工程设计和客户要求)。&lt;/p&gt;', '', '&lt;p style=&quot;white-space: normal;&quot;&gt;“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;复合土工膜&lt;/span&gt;”是用土工织物与土工膜复合而成的不透水材料，它主要用于防渗，复合土工膜是在薄膜的一侧或两侧经过烘箱远红外加热，把土工布和土工膜经导辊压到一起形成复合土工膜。随着生产工艺的提高，还有一种流延法做复合土工膜的工艺，其形式有“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;一布一膜&lt;/span&gt;”和“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两布一膜&lt;/span&gt;”“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;两膜一布&lt;/span&gt;”等。&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;复合土工膜抗拉、抗撕裂、顶破等物理力学性能指标高，能满足水利、市政、建筑、交通、地铁、隧道等土木工程需要。由于其选用高分子材料且生产工艺中添加了防老化剂，故可在非常规温度环境中使用。&lt;/p&gt;', '&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;抗拉、抗撕裂、顶破等物理力学性能指标高&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;产品具有强度高，延伸性能较好，变形模量大&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;耐酸碱、抗腐蚀，耐老化，防渗性能好等特点。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;良好的平面排水，优良的防渗特性。&lt;/p&gt;', '&lt;h4 style=&quot;white-space: normal;&quot;&gt;一、材料选择&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;复合土工膜材料质量检测。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;堤坡的清理、平整场地，一切尖角杂物，欠坡回填夯实、富坡削坡挖平后经监理验收合格，为复合土工膜铺设提供工作面。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;土工膜场内拼接为了施工方便，保证拼接质量，复合土工膜应尽量采用宽幅，减少现场拼接量，施工前应根据复合土工膜幅宽、现场长度需要，在单位内剪裁，并拼接成合符要求尺寸的块体，卷在钢管上，人工搬运到工作面铺设。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;二、复合土工膜的铺设与焊接方法&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;h5&gt;铺设技术&lt;/h5&gt;&lt;p&gt;1).铺设应在干燥和暖天气进行，复合土工膜铺设和展开方向，由E向W或由W向E进行，复合土工膜的铺设技术要求为了便于拼接，防止应力集中，复合土工膜铺设采用波浪形松弛方式，富余度约为1.5%，摊开后及时拉平，拉开，要求复合土工膜与坡面吻合平整，无突起褶皱，施工人员应穿平底布鞋或软胶鞋，严禁穿钉鞋，以免踩坏土工膜，施工时如发现土工膜损坏，应及时修补。 复合土工膜在运输过程中不要拖拉、硬拽，避免尖锐物刺伤。&lt;/p&gt;&lt;p&gt;2).铺设顺序，自渠道轴线向S、N两侧推进。&lt;/p&gt;&lt;p&gt;3).应从底部向高位延伸，不要拉得太紧，应留有1.50%的余幅，以备局部下沉拉伸。&lt;/p&gt;&lt;p&gt;4).相邻两幅的纵向接头不应在一条水平线上，应相互错开1m以上；&lt;/p&gt;&lt;p&gt;5).纵向接头应距离坝脚、弯脚处1.50m以上，应设在平面上；&lt;/p&gt;&lt;p&gt;6).先边坡后场底&lt;/p&gt;&lt;p&gt;7).边坡铺设时，展膜方向应基本平行于大坡度线。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;h5&gt;焊接技术&lt;/h5&gt;&lt;p&gt;1).焊道搭接宽度：80~100mm；平面和垂直面的自然褶皱分别为：5%~8%；预留伸缩量：3%~5%；边角料剩余量：2%~5%。&lt;/p&gt;&lt;p&gt;2).热熔焊接工作温度280~300℃；行进速度2~3m/min；焊接形式为双焊道。&lt;/p&gt;&lt;p&gt;3).破损部位修复方法，裁剪规格相同的材料，热熔粘补，聚乙烯胶密封。&lt;/p&gt;&lt;p&gt;4).焊道处无仿布的连接，采用机械缝合。&lt;/p&gt;&lt;p&gt;5).水下管口的密封止水，采用GB橡胶止水条密封，金属包扎并防腐处理。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;工序流程&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;p&gt;基础开挖&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;做库底砂浆保护层（H20-30mm）&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;削坡、密实与平整&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜与垂直挡水墙粘贴&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;基面平整、、工序交接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;水下水管口的密封止水&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜铺设与焊接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;工序交接&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;焊接与修补质量验收&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;竣工验收&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;h4 style=&quot;white-space: normal;&quot;&gt;复合土工膜的耐静水压测定&lt;/h4&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;width: 932.891px; white-space: normal;&quot;&gt;&lt;li&gt;&lt;h5&gt;复合土工膜的耐静水压原理&lt;/h5&gt;&lt;p&gt;在复合土工膜两侧压力水头达到值后，复合土工膜会破裂。逐级增加试样两侧水力压养，并保持时间，当渗流量急速增加，表示试样受到破坏，也获得了试样的耐静水压值。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;保持复合土工膜的上述压力少2h，观察渗流管水位变化情况，复合土工膜水位基本稳定（渗流量为0），则以0.1-0.2mpa，为级差逐级增加压力，每级均保持2h，直出现渗流量快速增加现象，表明试样已出现破裂，此前一级压力即作为耐静水压（mpa)。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜如只需判定土工膜样品是否达到某一规定耐静水压值，则可直接加压到此压力并保持2h，再判定是符合要求。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;复合土工膜每个样品少测定3个试样，以低值作为样品的耐静水压。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p style=&quot;white-space: normal;&quot;&gt;环保环卫工程（如生活垃圾填埋场、污水处理厂、有毒有害物处理场、危险品仓库、厂房等）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;水利土木工程（河湖水库堤坝防渗、堵漏、加固、水渠的防渗、垂直心墙、护坡等）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;市政工程（地铁、建筑物的地下工程和屋顶蓄水池、屋顶花园防渗、污水管道内衬等）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;园林绿化（人工湖、池塘、高尔夫球场的水塘底衬、护坡等）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;石化（化工厂、炼油厂储油池防渗防污、化学反应池、沉淀池的内衬、二次衬层）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;矿业（洗选池、堆浸池、堆灰场、尾矿坝防渗防污等）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;交通设施工程（公路的基础加固、涵洞的防渗）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;农业（水库、饮用水池、蓄水塘、灌溉系统的防渗）；&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;水产养殖业（鱼塘、虾池的内衬）等。&lt;/p&gt;', '&lt;p&gt;防渗、隔离、补强、防裂加固&lt;/p&gt;'),
(42, '', 'PP焊接土工格栅', '', '1523861027', '5ae2fce9f178b.jpg', 3, 0, 0, '&lt;p&gt;单向PP焊接土工格栅技术参数&lt;/p&gt;&lt;p&gt;参照JT/T480-2002&lt;/p&gt;&lt;table class=&quot;table table-bordered&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;项目&lt;/p&gt;&lt;/td&gt;&lt;td height=&quot;22&quot; align=&quot;left&quot; valign=&quot;center&quot; colspan=&quot;7&quot; style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot;&gt;&lt;p&gt;规格&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;名称GDZ&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;25&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;40&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;60&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;80&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;100&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding-top: 2px; padding-bottom: 2px; line-height: 22px; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p&gt;125&lt